import React, { Component } from "react";
import axios from "axios";

export class HTTPRequests extends Component {
	constructor(props) {
		super(props);

		this.state = {
			posts: [],
		};
	}

	componentDidMount() {
		axios
			.get("https://jsonplaceholder.typicode.com/posts")
			.then((response) => response.json())
			.then((json) =>
				this.setState({
					posts: json,
				})
			);
	}
	render() {
		return (
			<div>
				<h2>Posts</h2>
				<p>{posts}</p>
			</div>
		);
	}
}

export default HTTPRequests;

