import React, { Component } from "react";
import axios from "axios";

export class HTTPRequests extends Component {
	constructor(props) {
		super(props);

		this.state = {
			posts: [],
			error: "",
		};
	}

	componentDidMount() {
		axios
			.get("https://jsonplaceholder.typicode.com/posts/1s")
			.then((response) => {
				console.log(response);
				this.setState({
					posts: Array.isArray(response.data) ? response.data : [response.data],
				});
			})
			.catch((error) => {
				this.setState({
					error: error.message,
				});
			});
	}
	render() {
		const posts = this.state.posts;
		// return (
		// 	<div>
		// 		<h2>Posts</h2>
		// 		{/* turns the response into one string value */}
		// 		{/* <p>{JSON.stringify(this.state.posts)}</p> */}
		// 		{posts.map((book) => {
		// 			return (
		// 				<div key={book.id}>
		// 					<h4>{book.title}</h4>
		// 					<p>userId: {book.userId}</p>
		// 					<p>Id: {book.id}</p>
		// 					<p>{book.body}</p>
		// 				</div>
		// 			);
		// 		})}
		// 	</div>
		// );

		return (
			<div>
				<h2>Posts</h2>
				{/* turns the response into one string value */}
				{/* <p>{JSON.stringify(this.state.posts)}</p> */}
				{posts.length ? (
					posts.map((book) => (
						<div key={book.id}>
							<h4>{book.title}</h4>
							<p>userId: {book.userId}</p>
							<p>Id: {book.id}</p>
							<p>{book.body}</p>
						</div>
					))
				) : (
					<h4>Loading posts....</h4>
				)}
			</div>
		);
	}
}

export default HTTPRequests;

