		this.state = {
			isLoggedIn: true,
		};
	}

	// best pratice
	handleButtonClick = () => {
		this.setState(
			(prevState) => ({
				isLoggedIn: prevState.isLoggedIn ? false : true,
			}),
			() => console.log(this.state.isLoggedIn)
		);
	};

	// not best pratice
