import React, { useState } from "react";

function UseStateWithArrays() {
	const [nums, setnums] = useState([0, 1]);

	const updateNum = () => {
		setnums([...nums, nums + 1]);
	};
	return (
		<div>
			<button type="button" class="btn btn-primary" onClick={updateNum}>
				Button
			</button>
			<h1>{nums}</h1>
			<ul>
				{nums.map((num) => (
					<li key={num}>{num}</li>
				))}
			</ul>
		</div>
	);
}

export default UseStateWithArrays;
